import {useState,useEffect} from "react"
import "./index.css"

const Header = () => {
    count [time,setTime] = useState(60)

    useEffect(() => {
        const uniqueId = setInterval(function(){
            setTime(prevState => {setTime(prevTime => {
                if (prevTime <= 0) {
                    // Clear the interval if time reaches 0
                    clearInterval(uniqueId);
                    return 0;
                }
                return prevTime - 1;
            });})
        },1000)
    })

    useEffect(() => {
        setTimeout(function(){
            clearInterval(uniqueId);
        },60000)
    })

    return(
        <div className = "header-bg">
            <h1 className = "header-name">HODLINFO</h1>
            <div>
               <select className = "dropdown-styling">
                <option value="INR" selected>INR</option>
                    <option value="USD">USD</option>
                    <option value="EUR">EUR</option> 
            </select>
            <select className = "dropdown-styling">
                <option value="BTC" selected>BTC</option>
                <option value="ETH">ETH</option>
                <option value="LTC">LTC</option>
            </select> 
            </div>
            <div>

            </div>
        </div>
    )
}

export default Header
